#include &lt;stdio.h&gt;
main()
{
      int a, b, c, d;
      int area, length;

      printf(&quot;請分別輸入四個整數\n&quot;);
      printf(&quot;必須要保證 c &gt; a 和 d &gt; b\n&quot;); 

      printf(&quot;a = &quot;);
      scanf(&quot;%d&quot;, &amp;a);

      printf(&quot;b = &quot;);
      scanf(&quot;%d&quot;, &amp;b);

      printf(&quot;c = &quot;);
      scanf(&quot;%d&quot;, &amp;c);

      printf(&quot;d = &quot;);
      scanf(&quot;%d&quot;, &amp;d);


      area = (c-a) * (d-b);            // 計算面積
      length = 2 * ((c-a) + (d-b));    // 計算周長 

      printf(&quot;面積為：%d\n&quot;, area);
      printf(&quot;周長為：%d&quot;, length); 
}

